import React, {useEffect}from 'react'
import Button from 'react-bootstrap/Button';
function History() {
  const [histories, setHistories] = React.useState([]);
  useEffect(() => {
    fetch("/fecthtaskhistory").then((response) =>
      response.json().then((data) => {
        setHistories(data.tasks);
      })
    );
  }, []);
  console.log(histories);

  return (
    <div>
    {histories.map(task => {
      return (
      <div className='row' key={task.id} style={{marginTop:'60px'}}>
         <h4 style={{marginBottom:'40px'}}>Account History</h4>
        {/* account history  */}
        <div className='row' style={{marginBottom:'30px'}}>
          <div className='col-4'>
          <div>Filter By Date </div>
          </div>
          <div className='col-4'>
          <div>Filter By Location </div>
          </div>
          <div className='col-4'>
          <Button variant="primary">Filter</Button>{' '}
          </div>

        </div>
        <div className='col-10'>

          <table className='table table-striped' >
            <thead>
              <tr style={{color:'white', backgroundColor:'blue'}} >
                <th scope='col'>Date </th> 
                <th scope='col'>Description</th>
                <th scope='col'>Customer Name</th>
                <th scope='col'>Amount</th>
                <th scope='col'> Location</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td >1/1/2020</td>
                <td>Deposit</td>
                <td>Femi Olawale</td>
                <td>1000</td>
                <td>Abuja</td>

              </tr>
              <tr>
                <td>1/2/2020</td>
                <td>Withdrawal</td>
                <td>Mr Iliam </td>
                <td>500</td>
                <td>Kogi</td>
              </tr>
              <tr>
                <td>1/3/2020</td>
                <td>Deposit</td>
                <td>Mrs Ojeleye</td>
                <td>1000</td>
                <td>Enugu</td>
              </tr>
            </tbody>
          </table>

      </div>

    </div>
    );
  })}
  </div>
  )
}

export default History
