import React, { useContext, useState} from 'react'
import {Context} from '../Components/store/actions/appContext';


import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import '../Styles/Login.css'
import { RadioGroup } from '@mui/material';
import { FormControlLabel } from '@mui/material';
import { Radio } from '@mui/material';
import {useNavigate} from 'react-router-dom';



export const Login =() => {

// I DESTRUCTURED THE CONTEXT PROVIDER AND UTILISED ONLY ACTIONS
  const {actions} = useContext(Context);
// I DESTRUCTURED THE CONTEXT PROVIDER AND UTILISED ONLY ACTIONS

  ////
  const [error, setError] = useState(null);
  // const [Loading, setLoading] = useState(false);
  // const [role, setRole] = useState('user');

  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState("");

  const token = sessionStorage.getItem('token');

  const handleClick = async (e) => {
    e.preventDefault();
    // setLoading(true);

    // ATTENTION: DONT CALL ATION BECAUSE IT AN OBJECT OF OBJECTS SO YOU CALL WHAT YOU NEED IN IT WHICH IN THIS CASE IS {LOGIN}
    // HAPPY CODING
    const response = await actions.login(email, password);
    // ATTENTION: DONT CALL ATION BECAUSE IT AN OBJECT OF OBJECTS SO YOU CALL WHAT YOU NEED IN IT WHICH IN THIS CASE IS {LOGIN}
    // HAPPY CODING

    if (response.status === 200) {
      navigate('/home');
    } else {
      setError(response.data.message);
    }
    // setLoading(false);
  };  



  return (
    <main className='container' id='majordiv'>
      {token && token!= "" && token != undefined ? ("you are logged in with this token " + token 
      ) : (
      <div>
       <Form>
       <div class="col-md-4">
        <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="Enter email" value={email} onChange={(e) => setEmail(e.target.value)}  />
        <Form.Text className="text-muted">
          We'll never share your email with anyone else.
        </Form.Text>
      </Form.Group>
      </div>
      <div class="col-md-4">
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control type="password" placeholder="Password"  value={password} onChange={(e) => setPassword(e.target.value)} />
      </Form.Group>
        </div>
    <RadioGroup name="use-radio-group" defaultValue="first">
  <FormControlLabel value="first" label="Principal" control={<Radio />} />
  <FormControlLabel value="second" label="Proxy" control={<Radio />} />
  <FormControlLabel value="third" label="Corporate" control={<Radio />} />
</RadioGroup>
      <Button variant="primary" type="submit" onClick={handleClick} >
        Submit
      </Button>
    </Form>

    <div className='col-md-4'> 
        <span>Don't have an account? <a href='/Register'>Register</a></span>
    </div>

    </div>
     )} 

    </main>
  )

}





export default Login
